script:
frappe.ui.form.on('Airplane Ticket', {
    refresh: function(frm) {
        frm.page.add_action_item(__('Assign Seat'), () => {
            let d = new frappe.ui.Dialog({
                title: 'Assign Seat Number',
                fields: [
                    {
                        label: 'Seat Number',
                        fieldname: 'seat_number',
                        fieldtype: 'Data',
                        reqd: 1
                    }
                ],
                primary_action_label: 'Assign Seat',
                primary_action(values) {
                    frm.set_value('seat', values.seat_number);

                    // Optional: Unhide the seat field if it's hidden
                    frm.set_df_property('seat', 'hidden', 0);

                    d.hide();
                }
            });

            d.show();
        });
    }
});
